<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);    $id = $_POST["id"];    $done = $_POST["done"];    $time = $_POST["time"];    $user_id = $_POST["user_id"];    $notepad = $_POST["notepad"];    $date = $_POST["date"];        $statement = mysqli_prepare($con, "INSERT INTO user_cardio (id, done, time, user_id, notepad, date) VALUES (?, ?, ?, ?, ?, ?)");    mysqli_stmt_bind_param($statement, "iisiss", $id, $done, $time, $user_id, $notepad, $date);    mysqli_stmt_execute($statement);    $response = array();    $response["success"] = true;    echo json_encode($response);    mysqli_close($con);