<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'], $db['database']);    $sql = "SELECT * FROM list_cardio ORDER BY 'name' DESC LIMIT 50";    $result = mysqli_query($con, $sql);    $response = array();    $response = array();            $result = mysqli_query($con, $sql);            while ($row = mysqli_fetch_array($result)) {                  array_push($response, array(                    "id"=>$row[0],                     "name"=>$row[1],                    "calories"=>$row[2],                    ));            }    echo json_encode(array("server_response"=>$response));    mysqli_close($con);?>