<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'], $db['database']);    $id = $_GET["id"];    $user_id = $_GET["user_id"];    $date = $_GET["date"];    $sql = "SELECT * FROM list_cardio WHERE id = '$id' ORDER BY 'name' ";    $result = mysqli_query($con, $sql);    $sql1 = "SELECT * FROM user_cardio WHERE id = '$id' AND user_id='$user_id' AND date LIKE '$date' ";    $result1 = mysqli_query($con, $sql1);    $response = array();            while ($row = mysqli_fetch_array($result))             {                $row1 = mysqli_fetch_array($result1);                  array_push($response, array                    (                    "id"=>$row[0],                     "name"=>$row[1],                    "calories"=>$row[2],                    "done"=>$row1[1],                    "time"=>$row1[2],                    "notepad"=>$row1[4]                    )                );            }    echo json_encode(array("server_response"=>$response));    mysqli_close($con);