<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);    $product_id = $_POST["product_id"];    $sql = "SELECT * FROM list_products WHERE product_id='$product_id'";    $result = mysqli_query($con, $sql);        $response = array();    $info = array("1","2");    while ($row = mysqli_fetch_array($result)) {    array_push($response, array(        "product_id"=>$row[0],         "name"=>$row[1],         "weight"=>$row[2],         "proteins"=>$row[3],         "fats"=>$row[4],         "carbs"=>$row[5],         "fats_saturated"=>$row[7],        "fats_unsaturated"=>$row[8],        "carbs_fiber"=>$row[9],        "carbs_sugar"=>$row[10],        "multiplier_piece"=>$row[11],        "verified"=>$row[12]));    }    echo json_encode(array("info"=>"list_products WHERE product_id = $product_id", "server_response"=>$response));    mysqli_close($con);