<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'], $db['database']);        require_once('../Utils/SetCharset.php');    $id = $_POST["id"];    $updateresult = $_POST["updateresult"];    $date = $_POST["date"];        $sql = "SELECT * FROM user_calories WHERE id='$id' ORDER BY date DESC LIMIT 1";    $sql1 = "SELECT * FROM user_calories WHERE id='$id' AND date='$date'";    $result = mysqli_query($con, $sql);    $result1 = mysqli_query($con, $sql);    $result2 = mysqli_query($con, $sql);    $row1 = mysqli_fetch_array($result1,MYSQLI_ASSOC);    // printf("id: %s\n id: %s\n weight: %s\n height: %s\n somatotype: %s\n date: %s", $row1["id"], $row1["id"], $row1["weight"], $row1["height"], $row1["somatotype"], $row1["date"]);    // $id = $row1["id"];    // $height = $row1["height"];    if ($stmt = mysqli_prepare($con, $sql1)) 	{        mysqli_stmt_execute($stmt);        mysqli_stmt_store_result($stmt);    }    if (mysqli_stmt_num_rows($stmt) > 0) 	{        $sql2 = "UPDATE user_calories SET RESULT='$updateresult' WHERE id='$id' AND date='$date'";        mysqli_query($con,$sql2);    }    elseif (mysqli_stmt_num_rows($stmt) == 0)  	{    $statement = mysqli_prepare($con, "INSERT INTO user_calories (id, RESULT, date) VALUES (?, ?, ?)");    mysqli_stmt_bind_param($statement,"iss", $id, $updateresult, $date);    mysqli_stmt_execute($statement);    }    echo json_encode("user_calories UPDATE 'RESULT' row WHERE id = $id, id = $id, updateresult = $updateresult, date = $date");    mysqli_close($con);