<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);    require_once('../Utils/SetCharset.php');    $id = $_POST["id"];    $weight = $_POST["weight"];   	$user_id = $_POST["user_id"];    $date = $_POST["date"];    $statement = mysqli_prepare($con, "INSERT INTO user_diet (id, weight, user_id, date) VALUES (?, ?, ?, ?)");    mysqli_stmt_bind_param($statement, "iiss", $id, $weight, $user_id, $date);    mysqli_stmt_execute($statement);    $response = array();    $response["success"] = true;    echo json_encode($response);    mysqli_close($con);