<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);    require_once('../Utils/SetCharset.php');$name = $_GET["name"];    $sql = "SELECT * FROM list_products WHERE name LIKE '%$name%' ORDER BY 'name' DESC LIMIT 20";    $result = mysqli_query($con, $sql);    $response = array();	while ($row = mysqli_fetch_array($result))     {    	array_push($response, array(            "product_id"=>$row[0],             "name"=>$row[1],            "kcal"=>$row[6],            "verified"=>$row[12]        )    );	}    echo json_encode(array("server_response"=>$response));    mysqli_close($con);?>