<?php    // Will check if the file has already been included, and if so, not include (require) it again.    require_once('../config.php');    // Creating array whitch represents database connection info.    $db = $config['db'];    // This variable contains function whitch opens a new connection to the MySQL server.    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);        $id = $_GET["id"];    $sql_user_calories = "SELECT RESULT,date FROM user_calories WHERE id = $id ORDER BY date ASC";    $sql_query_calories = mysqli_query($con, $sql_user_calories);    $sql_user_cal_limit = "SELECT RESULT, date FROM user_calories_limit WHERE id = $id ORDER BY date ASC";    $sql_query_calories_limit = mysqli_query($con, $sql_user_cal_limit);    $calories = array();    $response1 = array();    $calories_limit = array();    while ($row = mysqli_fetch_array($sql_query_calories)) {        array_push($calories,             array(                "RESULT"=>intval($row['RESULT']),                "date"=>$row['date']            )        );    }    while ($row = mysqli_fetch_array($sql_query_calories_limit)) {        array_push($calories_limit,             array(                "RESULT"=>intval($row['RESULT']),                "date"=>$row['date']            )        );    }    if ($stmt = mysqli_prepare($con, $sql_user_calories)) {     mysqli_stmt_execute($stmt);     mysqli_stmt_store_result($stmt);    }    if (mysqli_stmt_num_rows($stmt) == 0)     {     $response1["noresult"] = true;     echo json_encode($response1);    }    elseif (mysqli_stmt_num_rows($stmt) > 0)     {        echo json_encode(array("server_response"=>$calories, "calories_limit"=>$calories_limit));    }?>