<?php    $con = mysqli_connect("mysql.hostinger.com", "u983372861_user", "Lukasz1989!", "u983372861_data");       $username = $_POST["username"];    // $username = 'burek';    $sql = "SELECT * FROM user_infomations WHERE username='$username'";    $result = mysqli_query($con, $sql);    $response = array();    $response1 = array();    while ($row = mysqli_fetch_array($result)) {        array_push($response, array("username"=>$row[1], "weight"=>$row[2], "height"=>$row[3], "somatotype"=>$row[4], "date"=>$row[5]));    }       if ($stmt = mysqli_prepare($con, $sql)) {        mysqli_stmt_execute($stmt);        mysqli_stmt_store_result($stmt);    }    if (mysqli_stmt_num_rows($stmt) == 0) {        $response1["noresult"] = true;        echo json_encode($response1);    }    elseif (mysqli_stmt_num_rows($stmt) > 0) {        echo json_encode(array("server_response"=>$response));    }    mysqli_close($con);?>