<?php    // Will check if the file has already been included, and if so, not include (require) it again.    require_once('../config.php');    // Creating array whitch represents database connection info.    $db = $config['db'];    // This variable contains function whitch opens a new connection to the MySQL server.    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);    // Variables from fetched hashmap.    $id = $_GET["id"];    $table = $_GET["table"];    $response = array();    if ($table == "user_height" || $table == "user_weight" || $table == "user_somatotype" || $table == "user_calories_limit")     {        // Selecting the last date row        $sql = "SELECT RESULT FROM `$table` WHERE id= '$id' ORDER BY date DESC LIMIT 1";        $result = mysqli_query($con, $sql);            // Fetching array with id, RESULT and date.        while ($row = mysqli_fetch_array($result))         {            array_push($response, array(                "RESULT"=>$row['RESULT']                )            );        }    }    else    {        // SELECT `proteins`,`fats`,`carbs` FROM `user_diet_ratio` WHERE `id`='5' ORDER BY `date` DESC LIMIT 1;        // Selecting the last date row        $sql = "SELECT proteins, fats, carbs FROM `$table` WHERE id= '$id' ORDER BY date DESC LIMIT 1";        $result = mysqli_query($con, $sql);        // Fetching array with diet_ratio.        while ($row = mysqli_fetch_array($result))         {            $proteins = $row['proteins'];            $fats = $row['fats'];            $carbs = $row['carbs'];            array_push($response, array(                "proteins"=>$proteins,                "fats"=>$fats,                "carbs"=>$carbs                )            );        }    }    // Generating JSON response whitch will contain     echo json_encode(array("server_response"=>$response));    mysqli_close($con);    // {    //     "server_response": [    //         {    //             "RESULT": "92.2",    //         }    //     ]    // }