<?php    // Will check if the file has already been included, and if so, not include (require) it again.    require_once('../config.php');    // Creating array whitch represents database connection info.    $db = $config['db'];    // This variable contains function whitch opens a new connection to the MySQL server.    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);    // Variables from fetched hashmap.    $id = $_GET["id"];    $response = array();    // Selecting the last date row from user_weight    $sql_weight = "SELECT RESULT FROM user_weight WHERE id= '$id' ORDER BY date DESC LIMIT 1";    $result_weight = mysqli_query($con, $sql_weight);    // Selecting the last date row from user_weight    $sql_height = "SELECT RESULT FROM user_height WHERE id= '$id' ORDER BY date DESC LIMIT 1";    $result_height = mysqli_query($con, $sql_height);    // Selecting the last date row from user_somatotype    $sql_somatotype = "SELECT RESULT FROM user_somatotype WHERE id= '$id' ORDER BY date DESC LIMIT 1";    $result_somatotype = mysqli_query($con, $sql_somatotype);    // Selecting auto_calories row from user_settings    $sql_auto_calories = "SELECT auto_calories FROM user_settings WHERE id= '$id' ";    $result_auto_calories = mysqli_query($con, $sql_auto_calories);    // Selecting the last date row from user_somatotype    $sql_calories_limit = "SELECT RESULT FROM user_calories_limit WHERE id= '$id' ORDER BY date DESC LIMIT 1";    $result_calories_limit = mysqli_query($con, $sql_calories_limit);    // Selecting the last date row from user_diet_ratio    $sql_diet_ratio = "SELECT proteins , fats , carbs FROM user_diet_ratio WHERE id= '$id' ORDER BY date DESC LIMIT 1";    $result_diet_ratio = mysqli_query($con, $sql_diet_ratio);    // Selecting diet_goal row from user_settings    $sql_diet_goal = "SELECT diet_goal FROM user_settings WHERE id= '$id' ";    $result_diet_goal = mysqli_query($con, $sql_diet_goal);    // Fetching array with RESULT.    while ($row = mysqli_fetch_array($result_weight))     {        array_push($response, array(            "weight"=>$row['RESULT']            )        );    }    // Fetching array with RESULT.    while ($row = mysqli_fetch_array($result_height))     {        array_push($response, array(            "height"=>$row['RESULT']            )        );    }    // Fetching array with RESULT.    while ($row = mysqli_fetch_array($result_somatotype))     {        array_push($response, array(            "somatotype"=>$row['RESULT']            )        );    }    while ($row = mysqli_fetch_array($result_auto_calories))     {        array_push($response, array(            "auto_calories"=>$row['auto_calories']            )        );        }    while ($row = mysqli_fetch_array($result_calories_limit))     {        array_push($response, array(            "calories_limit"=>$row['RESULT']            )        );        }    while ($row = mysqli_fetch_array($result_diet_ratio))     {        $ratio = $row['proteins'] . ":" . $row['fats'] . ":" . $row['carbs'];        array_push($response, array(            "diet_ratio"=>$ratio            )        );        }    while ($row = mysqli_fetch_array($result_diet_goal))     {        array_push($response, array(            "diet_goal"=>$row['diet_goal']            )        );        }    // Generating JSON response whitch will contain     echo json_encode(array("server_response"=>$response));    mysqli_close($con);    // {    //     "server_response": [    //         {    //             "RESULT": "180",    //         }    //     ]    // }