<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);    $id = $_POST["id"];    $done = $_POST["done"];    $rest = $_POST["rest"];    $reps = $_POST["reps"];    $weight = $_POST["weight"];   	$user_id = $_POST["user_id"];    $notepad = $_POST["notepad"];    $date = $_POST["date"];    $statement = mysqli_prepare($con, "INSERT INTO user_exercise (id, done, rest, reps, weight, user_id, notepad, date) VALUES (?, ?, ?, ?, ?, ?, ?, ?)");    mysqli_stmt_bind_param($statement, "iisssiss", $id, $done, $rest, $reps, $weight, $user_id, $notepad, $date);    mysqli_stmt_execute($statement);    $response = array();    $response["success"] = true;    echo json_encode($response);    mysqli_close($con);