<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);    $user_id = $_GET["user_id"];    $date = $_GET["date"];    $sql_exercise = "SELECT * FROM user_exercise WHERE user_id='$user_id' AND date LIKE '%$date%'";    $sql_cardio = "SELECT * FROM user_cardio WHERE user_id='$user_id' AND date LIKE '%$date%'";        $training_types = array();    $trainings_info = array();    $cardio_types = array();    $cardio_info = array();        if ($result = mysqli_query($con, $sql_exercise))     {        // fetch (sprowadzenie) associative table        // sprowadzenie wynikow by wyswietlic tablice asocjacynja    	while ($row = mysqli_fetch_assoc($result))         {            $id = $row["id"];            $sql1 = "SELECT * FROM list_excercise WHERE id = $id";            $result1 = mysqli_query($con, $sql1);                while ($row1 = mysqli_fetch_array($result1))                 {                    array_push($training_types, array(                        "id"            =>$row1[0],                        "name"          =>$row1[1],                         "target"        =>$row1[2]                        )                    );                }        }        }    $result2 = mysqli_query($con, $sql_exercise);        while ($row2 = mysqli_fetch_array($result2))        {            array_push($trainings_info, array(                "id"        =>$row2[0],                 "done"      =>$row2[1],                 "rest"      =>$row2[2],                 "reps"      =>$row2[3],                 "weight"    =>$row2[4],                 "date"      =>$row2[7]                )            );        }    if ($result3 = mysqli_query($con, $sql_cardio))     {        // fetch (sprowadzenie) associative table        // sprowadzenie wynikow by wyswietlic tablice asocjacynja        while ($row3 = mysqli_fetch_assoc($result3))         {            $id = $row3["id"];            $sql2 = "SELECT * FROM list_cardio WHERE id = $id";            $result1 = mysqli_query($con, $sql2);                while ($row1 = mysqli_fetch_array($result1))                {                    array_push($cardio_types, array(                        "id"            =>$row1[0],                        "name"          =>$row1[1],                         "calories"      =>$row1[2]                        )                    );                }        }        }    $result4 = mysqli_query($con, $sql_cardio);    while ($row4 = mysqli_fetch_array($result4))    {        array_push($cardio_info, array(            "id"        =>$row4[0],             "done"      =>$row4[1],             "time"      =>$row4[2],             "date"      =>$row4[5]            )        );    }    echo json_encode(array(        "training_types"=>$training_types,         "trainings_info"=>$trainings_info,        "cardio_types"=>$cardio_types,        "cardio_info"=>$cardio_info        )    );mysqli_close($con);