<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);    $date = $_POST["date"];    $username = $_POST["username"];    // $date = '28.02.2018';    // $username = 'brus5';    list($day, $month, $year) = explode(".", $date, 3);    // list($day, $month, $year) = split(".", $date);    $sql = "SELECT * FROM user_excercise WHERE username='$username' AND date LIKE '%$date%'";        $response = array();    $trainings_info = array();        if ($result = mysqli_query($con, $sql)) {        // fetch (sprowadzenie) associative table        // sprowadzenie wynikow by wyswietlic tablice asocjacynja    	while ($row = mysqli_fetch_assoc($result)) {            $id = $row["id"];                        $sql1 = "SELECT * FROM list_excercise WHERE id = $id";            $result1 = mysqli_query($con, $sql1);                while ($row1 = mysqli_fetch_array($result1)) {                    array_push($response, array(                        "id"=>$row1[0],                        "name"=>$row1[1],                         "target"=>$row1[2]));                }        }        }    $result2 = mysqli_query($con, $sql);        while ($row2 = mysqli_fetch_array($result2)){                        array_push($trainings_info, array(                            "id"=>$row2[0],                            "done"=>$row2[1],                             "rest"=>$row2[2],                             "reps"=>$row2[3],                             "weight"=>$row2[4],                             "date"=>$row2[6],                                }echo json_encode(array("server_response"=>$response, "trainings_info"=>$trainings_info));mysqli_close($con);?>