<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'], $db['database']);    $date = $_POST["date"];    $username = $_POST["username"];    $sql = "SELECT * FROM list_excercise WHERE date='$date' AND username='$username'";        $response = array();            $result = mysqli_query($con, $sql);            while ($row1 = mysqli_fetch_array($result)) {                  array_push($response, array("id"=>$row1[0], "username"=>$row1[1],"date"=>$row1[2], "done"=>$row1[3], "description"=>$row1[4],"rest"=>$row1[5],"weight"=>$row1[6],"reps"=>$row1[7],));            }echo json_encode(array("server_response"=>$response));mysqli_close($con);