<?php//         Will check if the file has already been included, and if so, not include (require) it again.    require_once('../config.php');    // Creating array whitch represents database connection info.    $db = $config['db'];    // This variable contains function whitch opens a new connection to the MySQL server.    $con = mysqli_connect($db['host'], $db['user'], $db['password'],$db['database']);       $username = $_POST["username"];    $response = array();    $user_id;    $username;    $user_first_name;    $user_birthday;    $user_email;    $user_gener;    $auto_calories;    $diet_goal;    $sql_user = "SELECT * FROM user WHERE username='$username'";    $query_id = mysqli_query($con, $sql_user);    if ($query_id = mysqli_query($con, $sql_user))    {        while ($row = mysqli_fetch_assoc($query_id))        {            $user_id = $row["user_id"];            $username = $row["username"];            $user_first_name = $row["name"];            $user_birthday = $row["birthday"];            $user_email = $row["email"];            $user_gener = $row["male"];        }    }    $sql_autokcal = "SELECT * FROM user_settings WHERE id='$user_id'";    $query_autokcal = mysqli_query($con, $sql_autokcal);    if ($query_autokcal = mysqli_query($con, $sql_autokcal))    {        while ($row = mysqli_fetch_assoc($query_autokcal))        {            $auto_calories = $row["auto_calories"];            $diet_goal = $row["diet_goal"];        }    }    echo json_encode(array        (            "user_id" => $user_id,            "username"=> $username,            "user_first_name"=> $user_first_name,            "user_birthday"=> $user_birthday,            "user_email"=> $user_email,            "user_gender"=> $user_gener,            "user_auto_calories"=> $auto_calories,            "user_diet_goal"=> $diet_goal        )    );