<?php    require_once('../config.php');    $db = $config['db'];    $con = mysqli_connect($db['host'], $db['user'], $db['password'], $db['database']);    $response = array();                 // Check connection        if (mysqli_connect_error())         {            $response["connected"] = false;            echo json_encode($response);            die("Connection failed: " . mysqli_connect_error());        }        else        {            $response["connected"] = true;            echo json_encode($response);        }            mysqli_close($con);?>